#include "DisplayController.h"#include "GPIODriver.h"#include "VirtualTimerManager.h"#include "gpio.h"#include "main.h"#include "tim.h"#include "usart.h"GPIO_TypeDef* segmentPorts[8] = {    GPIOB, // SEG_A    GPIOB, // SEG_B    GPIOF, // SEG_C    GPIOB, // SEG_D    GPIOA, // SEG_E    GPIOB, // SEG_F    GPIOF, // SEG_G    GPIOA  // SEG_DP};uint16_t segmentPins[8] = {    GPIO_PIN_13, // SEG_A    GPIO_PIN_7,  // SEG_B    GPIO_PIN_6,  // SEG_C    GPIO_PIN_15, // SEG_D    GPIO_PIN_11, // SEG_E    GPIO_PIN_14, // SEG_F    GPIO_PIN_7,  // SEG_G    GPIO_PIN_8   // SEG_DP};GPIO_TypeDef* digitPorts[16] = {    GPIOC, // GRID_1    GPIOC, // GRID_2    GPIOC, // GRID_3    GPIOB, // GRID_4    GPIOB, // GRID_5    GPIOB, // GRID_6    GPIOB, // GRID_7    GPIOB, // GRID_8    GPIOB, // GRID_9    GPIOB, // GRID_10    GPIOB, // GRID_11    GPIOB, // GRID_12    GPIOA, // GRID_13    GPIOA, // GRID_14    GPIOB, // GRID_15    GPIOA  // GRID_16};uint16_t digitPins[16] = {    GPIO_PIN_13, // GRID_1    GPIO_PIN_14, // GRID_2    GPIO_PIN_15, // GRID_3    GPIO_PIN_9,  // GRID_4    GPIO_PIN_12, // GRID_5    GPIO_PIN_10, // GRID_6    GPIO_PIN_1,  // GRID_7    GPIO_PIN_2,  // GRID_8    GPIO_PIN_6,  // GRID_9    GPIO_PIN_5,  // GRID_10    GPIO_PIN_8,  // GRID_11    GPIO_PIN_4,  // GRID_12    GPIO_PIN_9,  // GRID_13    GPIO_PIN_10, // GRID_14    GPIO_PIN_11, // GRID_15    GPIO_PIN_12  // GRID_16};DisplayController display(new GPIODriver(segmentPorts, segmentPins, digitPorts, digitPins));VirtualTimerManager timerManager(htim6, TIM6_IRQn, 2000);int numbers = 0;int app(){  timerManager.init();  display.initialize();  display.clearDisplay();//  display.displayChar("B.12", 1);  timerManager.addTimer(1, true, [](){    display.update();  });  timerManager.addTimer(100, true, [](){    display.displayDigit(numbers, 1);    numbers++;  });  while(true){  }}