#include "BaseTimer.h"BaseTimer* BaseTimer::instance = nullptr;BaseTimer::BaseTimer(TIM_HandleTypeDef &hw_timer, IRQn_Type irqNumber, uint32_t tick_frequency)    : hwTimer(hw_timer), irqNumber(irqNumber), tickFreq(tick_frequency) {}void BaseTimer::init()  {  HAL_TIM_Base_Start_IT(&hwTimer);  HAL_NVIC_SetPriority(this->irqNumber, 0, 0);  HAL_NVIC_EnableIRQ(this->irqNumber);  instance = this;}void BaseTimer::irqHandler() {    if (instance) {      instance->onTick();    }}void HAL_TIM_PeriodElapsedCallback(TIM_HandleTypeDef *htim){  if(htim->Instance == TIM6)  {    BaseTimer::irqHandler();  }}