#include "VirtualTimer.h"VirtualTimer::VirtualTimer() : duration(0), remaining(0), active(false), callback(nullptr) {}void VirtualTimer::start(uint32_t duration_ms, void (*callback)()) {  this->duration = duration_ms;  this->remaining = duration_ms;  this->callback = callback;  this->active = true;}void VirtualTimer::stop() {  this->active = false;}void VirtualTimer::tick() {  if (active && remaining > 0) {    remaining--;    if (remaining == 0) {      active = false;      if (callback) {        callback();      }    }  }}bool VirtualTimer::isActive() const {  return this->active;}